"use client"

import { useState, useEffect } from "react"
import { useParams, Link } from "react-router-dom"
import { paperService, solutionService } from "../services/api"
import { useAuth } from "../contexts/AuthContext"
import Spinner from "../components/common/Spinner"

const PaperDetailPage = () => {
  const { id } = useParams()
  const { isAuthenticated, user } = useAuth()

  const [paper, setPaper] = useState(null)
  const [solutions, setSolutions] = useState([])
  const [relatedPapers, setRelatedPapers] = useState([])
  const [newSolution, setNewSolution] = useState("")
  const [activeSolutionTab, setActiveSolutionTab] = useState("view") // 'view' or 'add'
  const [loading, setLoading] = useState(true)
  const [submitting, setSubmitting] = useState(false)
  const [error, setError] = useState("")

  useEffect(() => {
    const fetchPaperAndSolutions = async () => {
      try {
        setLoading(true)
        setError("")

        const paperResponse = await paperService.getPaperById(id)
        setPaper(paperResponse.data.paper)
        setRelatedPapers(paperResponse.data.relatedPapers)

        const solutionsResponse = await solutionService.getSolutions(id)
        setSolutions(solutionsResponse.data)
      } catch (err) {
        setError("Failed to fetch paper details. Please try again later.")
        console.error(err)
      } finally {
        setLoading(false)
      }
    }

    fetchPaperAndSolutions()
  }, [id])

  const handleDownload = () => {
    paperService.downloadPaper(id)
  }

  const handleSolutionSubmit = async (e) => {
    e.preventDefault()
    if (!newSolution.trim()) return

    try {
      setSubmitting(true)
      setError("")

      const response = await solutionService.addSolution(id, newSolution)

      setSolutions([response.data, ...solutions])
      setNewSolution("")
      setActiveSolutionTab("view")
    } catch (err) {
      setError("Failed to submit solution. Please try again later.")
      console.error(err)
    } finally {
      setSubmitting(false)
    }
  }

  const handleVote = async (solutionId, voteType) => {
    if (!isAuthenticated) return

    try {
      const voteFunction =
        voteType === "upvote"
          ? solutionService.upvoteSolution
          : solutionService.downvoteSolution

      const response = await voteFunction(solutionId)

      setSolutions(
        solutions.map((solution) =>
          solution._id === solutionId
            ? {
                ...solution,
                upvotes: response.data.upvotes,
                downvotes: response.data.downvotes,
              }
            : solution,
        ),
      )
    } catch (err) {
      console.error("Vote failed", err)
    }
  }

  if (loading) return <Spinner />

  if (!paper) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 text-center">
        <h2 className="text-2xl font-bold text-red-600">Paper not found</h2>
        <p className="mt-2 text-gray-600">
          The paper you're looking for doesn't exist or has been removed.
        </p>
        <Link
          to="/papers"
          className="mt-4 inline-block text-blue-600 hover:underline"
        >
          Back to papers
        </Link>
      </div>
    )
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="mb-6">
        <Link
          to="/papers"
          className="inline-flex items-center text-sm text-blue-600 hover:text-blue-500"
        >
          <svg
            className="h-5 w-5 mr-1"
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              strokeWidth={2}
              d="M10 19l-7-7m0 0l7-7m-7 7h18"
            />
          </svg>
          Back to papers
        </Link>
      </div>

      {error && (
        <div className="rounded-md bg-red-50 p-4 mb-6">
          <div className="flex">
            <div className="ml-3">
              <h3 className="text-sm font-medium text-red-800">{error}</h3>
            </div>
          </div>
        </div>
      )}

      <div className="lg:grid lg:grid-cols-3 lg:gap-8">
        <div className="lg:col-span-2">
          <div className="bg-white shadow overflow-hidden sm:rounded-lg mb-6">
            <div className="px-4 py-5 sm:px-6 flex justify-between items-start">
              <div>
                <h3 className="text-lg leading-6 font-medium text-gray-900">
                  {paper.title}
                </h3>
                <p className="mt-1 max-w-2xl text-sm text-gray-500">
                  {paper.subject} â€¢ {paper.semester} {paper.year}
                </p>
              </div>
              <button
                onClick={handleDownload}
                className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
              >
                <svg
                  className="-ml-1 mr-2 h-5 w-5"
                  xmlns="http://www.w3.org/2000/svg"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth={2}
                    d="M4 16v1a3 3 0 003 3h10a3 3 0 003-3v-1m-4-4l-4 4m0 0l-4-4m4 4V4"
                  />
                </svg>
                Download
              </button>
            </div>


            <div className="border-t border-gray-200">
              <dl>
                {paper.description && (
                  <div className="bg-gray-50 px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                    <dt className="text-sm font-medium text-gray-500">
                      Description
                    </dt>
                    <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                      {paper.description}
                    </dd>
                  </div>
                )}
                <div className="bg-white px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                  <dt className="text-sm font-medium text-gray-500">
                    Uploaded by
                  </dt>
                  <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                    {paper.author.name}
                  </dd>
                </div>
                <div className="bg-gray-50 px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                  <dt className="text-sm font-medium text-gray-500">
                    File name
                  </dt>
                  <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                    {paper.fileName}
                  </dd>
                </div>
                <div className="bg-gray-50 px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                  <dt className="text-sm font-medium text-gray-500">
                    Subject Code
                  </dt>
                  <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                    {paper.subjectCode}
                  </dd>
                </div>
                <div className="bg-gray-50 px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                  <dt className="text-sm font-medium text-gray-500">Semester</dt>
                  <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                    {paper.semester}
                  </dd>
                </div>
                <div className="bg-gray-50 px-4 py-5 sm:grid sm:grid-cols-3 sm:gap-4 sm:px-6">
                  <dt className="text-sm font-medium text-gray-500">Year</dt>
                  <dd className="mt-1 text-sm text-gray-900 sm:mt-0 sm:col-span-2">
                    {paper.year}
                  </dd>
                </div>
              </dl>
            </div>
          </div>

          <div className="mb-8">
            <h2 className="text-xl font-semibold mb-4">Solutions</h2>

            <div className="flex border-b border-gray-200 mb-4">
              <button
                className={`px-4 py-2 -mb-px border-b-2 font-medium text-sm ${
                  activeSolutionTab === "view"
                    ? "border-blue-600 text-blue-600"
                    : "border-transparent text-gray-500 hover:text-gray-700"
                }`}
                onClick={() => {
                  setActiveSolutionTab("view")
                  setError("")
                }}
              >
                View Solutions
              </button>
              <button
                className={`ml-4 px-4 py-2 -mb-px border-b-2 font-medium text-sm ${
                  activeSolutionTab === "add"
                    ? "border-blue-600 text-blue-600"
                    : "border-transparent text-gray-500 hover:text-gray-700"
                }`}
                onClick={() => {
                  if (!isAuthenticated) {
                    setError("Please login to add a solution.")
                    return
                  }
                  setActiveSolutionTab("add")
                  setError("")
                }}
              >
                Add Solution
              </button>
            </div>

            {activeSolutionTab === "view" && (
              <>
                {solutions.length === 0 && (
                  <p className="text-gray-500">
                    No solutions have been added for this paper yet.
                  </p>
                )}
                <ul className="space-y-4">
                  {solutions.map((solution) => {
                    const userUpvoted =
                      user && solution.upvotes.includes(user._id)
                    const userDownvoted =
                      user && solution.downvotes.includes(user._id)

                    return (
                      <li
                        key={solution._id}
                        className="bg-white shadow rounded-md p-4"
                      >
                        <div className="mb-2 text-gray-700 whitespace-pre-line">
                          {solution.text}
                        </div>
                        <div className="flex items-center space-x-4">
                          <button
                            onClick={() => handleVote(solution._id, "upvote")}
                            aria-label="Upvote solution"
                            className={`flex items-center space-x-1 text-sm ${
                              userUpvoted ? "text-green-600" : "text-gray-400"
                            } hover:text-green-700`}
                          >
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              className="h-5 w-5"
                              fill="none"
                              viewBox="0 0 24 24"
                              stroke="currentColor"
                              strokeWidth={2}
                            >
                              <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                d="M5 15l7-7 7 7"
                              />
                            </svg>
                            <span>{solution.upvotes.length}</span>
                          </button>
                          <button
                            onClick={() => handleVote(solution._id, "downvote")}
                            aria-label="Downvote solution"
                            className={`flex items-center space-x-1 text-sm ${
                              userDownvoted
                                ? "text-red-600"
                                : "text-gray-400"
                            } hover:text-red-700`}
                          >
                            <svg
                              xmlns="http://www.w3.org/2000/svg"
                              className="h-5 w-5"
                              fill="none"
                              viewBox="0 0 24 24"
                              stroke="currentColor"
                              strokeWidth={2}
                            >
                              <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                d="M19 9l-7 7-7-7"
                              />
                            </svg>
                            <span>{solution.downvotes.length}</span>
                          </button>
                          <span className="text-gray-500 text-xs">
                            By {solution.author.name}
                          </span>
                          <span className="text-gray-400 text-xs">
                            {new Date(solution.createdAt).toLocaleString()}
                          </span>
                        </div>
                      </li>
                    )
                  })}
                </ul>
              </>
            )}

            {activeSolutionTab === "add" && (
              <form onSubmit={handleSolutionSubmit}>
                <textarea
                  rows={5}
                  className="w-full p-3 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                  placeholder="Write your solution here..."
                  value={newSolution}
                  onChange={(e) => setNewSolution(e.target.value)}
                  required
                />
                <div className="mt-3">
                  <button
                    type="submit"
                    disabled={submitting}
                    className={`inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white ${
                      submitting
                        ? "bg-blue-300 cursor-not-allowed"
                        : "bg-blue-600 hover:bg-blue-700"
                    } focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500`}
                  >
                    {submitting ? "Submitting..." : "Submit Solution"}
                  </button>
                </div>
              </form>
            )}
          </div>
        </div>

        <aside className="lg:col-span-1">
          <div className="sticky top-24 space-y-4">
            <h3 className="text-lg font-semibold">Related Papers</h3>
            {relatedPapers.length === 0 && (
              <p className="text-gray-500">No related papers found.</p>
            )}
            <ul className="space-y-2">
              {relatedPapers.map((related) => (
                <li key={related._id}>
                  <Link
                    to={`/papers/${related._id}`}
                    className="text-blue-600 hover:underline"
                  >
                    {related.title}
                  </Link>
                </li>
              ))}
            </ul>
          </div>
        </aside>
      </div>
    </div>
  )
}

export default PaperDetailPage
