"use client"

import { useState } from "react"
import { Link, useNavigate } from "react-router-dom"
import { useAuth } from "../../contexts/AuthContext"

const Navbar = ({ isDark, setIsDark }) => {
  const { user, logout, isAuthenticated } = useAuth()
  const [isMenuOpen, setIsMenuOpen] = useState(false)
  const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false)
  const navigate = useNavigate()

  const handleLogout = () => {
    logout()
    navigate("/login")
  }

  return (
    <nav className="bg-background border-b border-border text-foreground">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16 items-center">
          {/* Logo and links */}
          <div className="flex items-center space-x-4">
            <Link to="/" className="text-xl font-bold text-primary">
              ExamRepo
            </Link>
            <div className="hidden sm:flex sm:space-x-6">
              <Link to="/" className="hover:text-muted-foreground">Home</Link>
              <Link to="/papers" className="hover:text-muted-foreground">Papers</Link>
              {isAuthenticated && <Link to="/upload" className="hover:text-muted-foreground">Upload</Link>}
              <Link to="/about" className="hover:text-muted-foreground">About</Link>
            </div>
          </div>

          {/* Dark mode toggle */}
          <div className="flex items-center space-x-4">
            <button
              onClick={() => setIsDark(!isDark)}
              className="text-xl hover:text-primary"
              title={isDark ? "Light Mode" : "Dark Mode"}
            >
              {isDark ? '‚òÄÔ∏è' : 'üåô'}
            </button>

            {/* Auth buttons or user profile */}
            {isAuthenticated ? (
              <div className="relative">
                <button
                  type="button"
                  className="flex items-center text-sm rounded-full focus:outline-none"
                  onClick={() => setIsProfileMenuOpen(!isProfileMenuOpen)}
                >
                  <div className="h-8 w-8 rounded-full bg-blue-100 flex items-center justify-center text-blue-600">
                    {user?.name?.charAt(0)}
                  </div>
                  <span className="ml-2">{user?.name}</span>
                </button>
                {isProfileMenuOpen && (
                  <div className="absolute right-0 mt-2 w-48 bg-popover text-popover-foreground rounded-md shadow-lg ring-1 ring-black ring-opacity-5 z-10">
                    <Link to="/profile" className="block px-4 py-2 hover:bg-accent">Profile</Link>
                    <Link to="/my-papers" className="block px-4 py-2 hover:bg-accent">My Papers</Link>
                    <Link to="/my-solutions" className="block px-4 py-2 hover:bg-accent">My Solutions</Link>
                    <button
                      onClick={handleLogout}
                      className="block w-full text-left px-4 py-2 hover:bg-accent"
                    >
                      Logout
                    </button>
                  </div>
                )}
              </div>
            ) : (
              <div className="hidden sm:flex space-x-4">
                <Link to="/login" className="hover:text-primary">Login</Link>
                <Link
                  to="/register"
                  className="bg-primary text-white px-4 py-2 rounded-md hover:bg-primary/90"
                >
                  Sign up
                </Link>
              </div>
            )}

            {/* Hamburger for mobile */}
            <button
              onClick={() => setIsMenuOpen(!isMenuOpen)}
              className="sm:hidden text-2xl"
            >
              {isMenuOpen ? '‚úñÔ∏è' : '‚ò∞'}
            </button>
          </div>
        </div>
      </div>

      {/* Mobile Menu */}
      {isMenuOpen && (
        <div className="sm:hidden px-4 pt-2 pb-4 space-y-2 text-foreground bg-background">
          <Link to="/" className="block hover:text-muted-foreground">Home</Link>
          <Link to="/papers" className="block hover:text-muted-foreground">Papers</Link>
          {isAuthenticated && <Link to="/upload" className="block hover:text-muted-foreground">Upload</Link>}
          <Link to="/about" className="block hover:text-muted-foreground">About</Link>
          <div className="border-t border-border mt-2 pt-2">
            {isAuthenticated ? (
              <>
                <Link to="/profile" className="block hover:text-muted-foreground">Profile</Link>
                <Link to="/my-papers" className="block hover:text-muted-foreground">My Papers</Link>
                <Link to="/my-solutions" className="block hover:text-muted-foreground">My Solutions</Link>
                <button
                  onClick={handleLogout}
                  className="block w-full text-left hover:text-muted-foreground"
                >
                  Logout
                </button>
              </>
            ) : (
              <>
                <Link to="/login" className="block hover:text-muted-foreground">Login</Link>
                <Link to="/register" className="block hover:text-muted-foreground">Sign up</Link>
              </>
            )}
          </div>
        </div>
      )}
    </nav>
  )
}

export default Navbar
