import { useEffect, useState } from "react"
import { useParams, Link } from "react-router-dom"

function PaperDetailPage() {
  const { id } = useParams()
  const [paper, setPaper] = useState(null)
  const [solutions, setSolutions] = useState([])
  const [relatedPapers, setRelatedPapers] = useState([])
  const [loading, setLoading] = useState(true)

  const [chatInput, setChatInput] = useState("")
  const [chatHistory, setChatHistory] = useState([])
  const [chatLoading, setChatLoading] = useState(false)

  const fetchPaperDetails = async () => {
    try {
      const res = await fetch(`/api/papers/${id}`)
      const data = await res.json()
      setPaper(data.paper)
      setSolutions(data.solutions)
      setRelatedPapers(data.relatedPapers)
    } catch (err) {
      console.error("Error fetching paper:", err)
    } finally {
      setLoading(false)
    }
  }

  useEffect(() => {
    fetchPaperDetails()
  }, [id])

  const handleChatSubmit = async (e) => {
    e.preventDefault()
    if (!chatInput.trim()) return

    const question = chatInput.trim()
    setChatInput("")
    setChatLoading(true)

    try {
      const res = await fetch("/api/chatbot/ask", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ question, paperId: id }),
      })

      const data = await res.json()
      setChatHistory([{ question, answer: data.answer }, ...chatHistory])
    } catch (err) {
      console.error("Chatbot error:", err)
      setChatHistory([{ question, answer: "Something went wrong. Please try again later." }, ...chatHistory])
    } finally {
      setChatLoading(false)
    }
  }

  if (loading) {
    return (
      <div className="text-center mt-10 text-lg text-gray-600">
        Loading paper details...
      </div>
    )
  }

  if (!paper) {
    return (
      <div className="text-center mt-10 text-red-500 text-lg">
        Paper not found.
      </div>
    )
  }

  return (
    <div className="max-w-7xl mx-auto p-4">
      <div className="bg-white shadow sm:rounded-lg overflow-hidden mb-6">
        <div className="px-4 py-5 sm:px-6">
          <h2 className="text-2xl font-bold text-gray-900 mb-2">{paper.title}</h2>
          <p className="text-sm text-gray-500">
            {paper.course} | {paper.branch} | Semester {paper.semester} | Year {paper.year}
          </p>
          <a
            href={paper.fileUrl}
            target="_blank"
            rel="noopener noreferrer"
            className="inline-block mt-3 px-4 py-2 bg-blue-600 text-white text-sm font-medium rounded hover:bg-blue-700"
          >
            Download Paper
          </a>
        </div>
      </div>

      <div className="lg:grid lg:grid-cols-3 lg:gap-8">
        {/* Main Section (Paper + Solutions) */}
        <div className="lg:col-span-2">
          <div className="bg-white shadow sm:rounded-lg overflow-hidden mb-6">
            <div className="px-4 py-5 sm:px-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900">Solutions</h3>
            </div>
            {solutions.length > 0 ? (
              <ul className="divide-y divide-gray-200">
                {solutions.map((solution) => (
                  <li key={solution._id}>
                    <div className="px-4 py-4 sm:px-6">
                      <div className="flex justify-between items-center">
                        <div>
                          <p className="text-sm font-medium text-gray-900">{solution.user?.name}</p>
                          <p className="text-sm text-gray-500">{solution.content}</p>
                        </div>
                        <div className="text-sm text-gray-500">Upvotes: {solution.upvotes}</div>
                      </div>
                    </div>
                  </li>
                ))}
              </ul>
            ) : (
              <div className="px-4 py-5 sm:px-6 text-sm text-gray-500">No solutions available yet.</div>
            )}
          </div>
        </div>

        {/* Right Sidebar */}
        <div className="mt-6 lg:mt-0 space-y-6">
          {/* Related Papers */}
          <div className="bg-white shadow sm:rounded-lg overflow-hidden">
            <div className="px-4 py-5 sm:px-6">
              <h3 className="text-lg leading-6 font-medium text-gray-900">Related Papers</h3>
            </div>
            {relatedPapers.length > 0 ? (
              <ul className="divide-y divide-gray-200">
                {relatedPapers.map((relatedPaper) => (
                  <li key={relatedPaper._id}>
                    <Link to={`/papers/${relatedPaper._id}`} className="block hover:bg-gray-50">
                      <div className="px-4 py-4 sm:px-6">
                        <div className="text-sm font-medium text-blue-600 truncate">{relatedPaper.title}</div>
                        <div className="mt-1 text-xs text-gray-500">
                          {relatedPaper.semester} {relatedPaper.year}
                        </div>
                      </div>
                    </Link>
                  </li>
                ))}
              </ul>
            ) : (
              <div className="px-4 py-5 sm:px-6 text-sm text-gray-500">No related papers found</div>
            )}
          </div>

          {/* Chatbot Section */}
          <div className="bg-white shadow sm:rounded-lg p-4">
            <h3 className="text-lg font-medium text-gray-900 mb-3">Ask Chatbot</h3>
            <div className="max-h-64 overflow-y-auto border p-2 rounded-md bg-gray-50 mb-3 space-y-2 text-sm">
              {chatHistory.map((chat, index) => (
                <div key={index}>
                  <div className="font-semibold text-blue-600">You:</div>
                  <div className="mb-1">{chat.question}</div>
                  <div className="font-semibold text-green-600">Bot:</div>
                  <div className="text-gray-700">{chat.answer}</div>
                </div>
              ))}
              {chatLoading && <div className="text-gray-500 italic">Thinking...</div>}
            </div>
            <form onSubmit={handleChatSubmit} className="flex gap-2">
              <input
                type="text"
                value={chatInput}
                onChange={(e) => setChatInput(e.target.value)}
                className="flex-1 px-3 py-1 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500 text-sm"
                placeholder="Ask a question..."
              />
              <button
                type="submit"
                disabled={!chatInput.trim() || chatLoading}
                className="px-3 py-1 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50 text-sm"
              >
                Ask
              </button>
            </form>
          </div>
        </div>
      </div>
    </div>
  )
}

export default PaperDetailPage
