"use client"

import { useState, useEffect } from "react"
import { useParams, Link } from "react-router-dom"
import { paperService, solutionService } from "../services/api"
import { useAuth } from "../contexts/AuthContext"
import Spinner from "../components/common/Spinner"

const PaperDetailPage = () => {
  const { id } = useParams()
  const { isAuthenticated, user } = useAuth()

  const [paper, setPaper] = useState(null)
  const [solutions, setSolutions] = useState([])
  const [relatedPapers, setRelatedPapers] = useState([])
  const [newSolution, setNewSolution] = useState("")
  const [activeSolutionTab, setActiveSolutionTab] = useState("view")
  const [loading, setLoading] = useState(true)
  const [submitting, setSubmitting] = useState(false)
  const [error, setError] = useState("")

  const [chatInput, setChatInput] = useState("")
  const [chatHistory, setChatHistory] = useState([])
  const [chatLoading, setChatLoading] = useState(false)

  useEffect(() => {
    const fetchPaperAndSolutions = async () => {
      try {
        setLoading(true)
        const paperResponse = await paperService.getPaperById(id)
        setPaper(paperResponse.data.paper)
        setRelatedPapers(paperResponse.data.relatedPapers)

        const solutionsResponse = await solutionService.getSolutions(id)
        setSolutions(solutionsResponse.data)
      } catch (err) {
        setError("Failed to fetch paper details. Please try again later.")
        console.error(err)
      } finally {
        setLoading(false)
      }
    }

    fetchPaperAndSolutions()
  }, [id])

  const handleDownload = () => {
    paperService.downloadPaper(id)
  }

  const handleSolutionSubmit = async (e) => {
    e.preventDefault()
    if (!newSolution.trim()) return

    try {
      setSubmitting(true)
      const response = await solutionService.addSolution(id, newSolution)
      setSolutions([response.data, ...solutions])
      setNewSolution("")
      setActiveSolutionTab("view")
    } catch (err) {
      setError("Failed to submit solution. Please try again later.")
      console.error(err)
    } finally {
      setSubmitting(false)
    }
  }

  const handleVote = async (solutionId, voteType) => {
    if (!isAuthenticated) return

    try {
      const voteFunction =
        voteType === "upvote" ? solutionService.upvoteSolution : solutionService.downvoteSolution
      const response = await voteFunction(solutionId)

      setSolutions(
        solutions.map((solution) =>
          solution._id === solutionId
            ? {
                ...solution,
                upvotes: response.data.upvotes,
                downvotes: response.data.downvotes,
              }
            : solution
        )
      )
    } catch (err) {
      console.error("Vote failed", err)
    }
  }

  const handleChatSubmit = async (e) => {
    e.preventDefault()
    if (!chatInput.trim()) return

    const question = chatInput.trim()
    setChatInput("")
    setChatLoading(true)

    try {
      const res = await fetch("/api/chatbot/ask", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ question, paperId: id }),
      })

      const data = await res.json()
      setChatHistory([{ question, answer: data.answer }, ...chatHistory])
    } catch (err) {
      console.error("Chatbot error:", err)
      setChatHistory([
        { question, answer: "Something went wrong. Please try again later." },
        ...chatHistory,
      ])
    } finally {
      setChatLoading(false)
    }
  }

  if (loading) return <Spinner />

  if (!paper) {
    return (
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12 text-center">
        <h2 className="text-2xl font-bold text-red-600">Paper not found</h2>
        <p className="mt-2 text-gray-600">
          The paper you're looking for doesn't exist or has been removed.
        </p>
        <Link to="/papers" className="mt-4 inline-block text-blue-600 hover:underline">
          Back to papers
        </Link>
      </div>
    )
  }

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      {/* Existing content above */}

      {/* ... your full Paper details + Solutions UI here (same as original) ... */}

      {/* Chatbot Section */}
      <div className="mt-10 bg-white shadow sm:rounded-lg p-6">
        <h3 className="text-lg font-medium text-gray-900 mb-4">Ask our Chatbot</h3>
        <div className="max-h-64 overflow-y-auto border p-4 rounded-md bg-gray-50 mb-4 space-y-3">
          {chatHistory.map((chat, index) => (
            <div key={index}>
              <div className="text-sm font-semibold text-blue-600">You:</div>
              <div className="text-sm mb-2">{chat.question}</div>
              <div className="text-sm font-semibold text-green-600">Bot:</div>
              <div className="text-sm text-gray-700">{chat.answer}</div>
            </div>
          ))}
          {chatLoading && (
            <div className="text-gray-500 text-sm italic">Thinking...</div>
          )}
        </div>
        <form onSubmit={handleChatSubmit} className="flex gap-2">
          <input
            type="text"
            value={chatInput}
            onChange={(e) => setChatInput(e.target.value)}
            className="flex-1 px-4 py-2 border border-gray-300 rounded-md focus:ring-blue-500 focus:border-blue-500"
            placeholder="Ask a question about this paper..."
          />
          <button
            type="submit"
            disabled={!chatInput.trim() || chatLoading}
            className="px-4 py-2 bg-blue-600 text-white rounded-md hover:bg-blue-700 disabled:opacity-50"
          >
            Ask
          </button>
        </form>
      </div>
    </div>
  )
}

export default PaperDetailPage
