"use client"

import { useState, useEffect } from "react"
import { Link } from "react-router-dom"
import { useAuth } from "../contexts/AuthContext"
import { paperService, solutionService } from "../services/api"
import Spinner from "../components/common/Spinner"

const ProfilePage = () => {
  const { user, updateProfile, updatePassword } = useAuth()

  const [name, setName] = useState("")
  const [email, setEmail] = useState("")
  const [bio, setBio] = useState("")
  const [avatar, setAvatar] = useState("")
  const [currentPassword, setCurrentPassword] = useState("")
  const [newPassword, setNewPassword] = useState("")
  const [confirmPassword, setConfirmPassword] = useState("")
  const [loading, setLoading] = useState(false)
  const [submitting, setSubmitting] = useState(false)
  const [error, setError] = useState("")
  const [success, setSuccess] = useState("")
  const [activeTab, setActiveTab] = useState("profile")
  // ... (keep all other existing state declarations)

  useEffect(() => {
    if (user) {
      setName(user.name || "")
      setEmail(user.email || "")
      setBio(user.bio || "")
      setAvatar(user.avatar || "")
    }
  }, [user])

  const handleProfileSubmit = async (e) => {
    e.preventDefault()
    setSubmitting(true)
    setError("")
    setSuccess("")

    try {
      await updateProfile({ name, email, bio, avatar })
      setSuccess("Profile updated successfully")
    } catch (err) {
      setError(err.response?.data?.message || "Failed to update profile")
    } finally {
      setSubmitting(false)
    }
  }

  // ... (keep all other existing functions)

  return (
    <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
      <div className="lg:grid lg:grid-cols-12 lg:gap-x-5">
        {/* Keep the sidebar navigation as is */}

        <div className="space-y-6 sm:px-6 lg:px-0 lg:col-span-9">
          {activeTab === "profile" && (
            <form onSubmit={handleProfileSubmit}>
              <div className="shadow sm:rounded-md sm:overflow-hidden">
                <div className="bg-white py-6 px-4 space-y-6 sm:p-6">
                  <div>
                    <h3 className="text-lg leading-6 font-medium text-gray-900">Profile Information</h3>
                    <p className="mt-1 text-sm text-gray-500">Update your personal information and profile details.</p>
                  </div>

                  {/* Error and success messages */}

                  <div className="grid grid-cols-6 gap-6">
                    {/* Profile Picture Section */}
                    <div className="col-span-6 flex items-center">
                      <div className="flex-shrink-0 h-16 w-16 rounded-full overflow-hidden">
                        {avatar ? (
                          <img
                            className="h-full w-full object-cover"
                            src={avatar}
                            alt="Profile"
                          />
                        ) : (
                          <div className="h-full w-full bg-gray-200 flex items-center justify-center">
                            <svg
                              className="h-8 w-8 text-gray-400"
                              xmlns="http://www.w3.org/2000/svg"
                              fill="none"
                              viewBox="0 0 24 24"
                              stroke="currentColor"
                            >
                              <path
                                strokeLinecap="round"
                                strokeLinejoin="round"
                                strokeWidth={2}
                                d="M16 7a4 4 0 11-8 0 4 4 0 018 0zM12 14a7 7 0 00-7 7h14a7 7 0 00-7-7z"
                              />
                            </svg>
                          </div>
                        )}
                      </div>
                      <div className="ml-4 flex-1">
                        <label
                          htmlFor="profile-picture-url"
                          className="block text-sm font-medium text-gray-700"
                        >
                          Profile Picture URL
                        </label>
                        <input
                          type="url"
                          id="profile-picture-url"
                          name="profile-picture-url"
                          value={avatar}
                          onChange={(e) => setAvatar(e.target.value)}
                          className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                          placeholder="https://example.com/profile.jpg"
                        />
                        <p className="mt-2 text-sm text-gray-500">
                          Paste the URL of your profile picture
                        </p>
                      </div>
                    </div>

                    {/* Keep the existing name, email, and bio fields */}
                    <div className="col-span-6 sm:col-span-3">
                      <label htmlFor="name" className="block text-sm font-medium text-gray-700">
                        Full name
                      </label>
                      <input
                        type="text"
                        name="name"
                        id="name"
                        autoComplete="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        className="mt-1 block w-full border border-gray-300 rounded-md shadow-sm py-2 px-3 focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                        required
                      />
                    </div>

                    {/* ... rest of the existing fields ... */}
                  </div>
                </div>
                {/* Keep the submit button */}
              </div>
            </form>
          )}

          {/* Keep all other tabs (security, myPapers, mySolutions) as is */}
        </div>
      </div>
    </div>
  )
}

export default ProfilePage
